mapper.select:
select   from adm_article a
WHERE 1=1
AND a.state = #{ft.state}
AND a.type = #{ft.type}
AND a.title like CONCAT('%',#{ft.kw},'%')
AND a.createtime > #{ft.startTime}
AND a.type < #{ft.endTime}
ORDER BY createtime DESC

mapper.update:
	  UPDATE adm_article SET title=#{ model.title }
,author=#{ model.author }
,image=#{ model.image }
,summary=#{ model.summary }
,content=#{ model.content }
 WHERE id=#{model.id}
