mapper.select:
		select  ,b.name productName,c.name empName,d.name typeName,e.name deptName from cus_customer a  left join cus_product b on a.productid=b.id
		left join man_emp c on a.empid=c.id
		left join man_dept e on c.deptid = e.id
		left join cus_type d on a.typeid = d.id
		WHERE 1=1
		AND a.state = #{ft.state}
		AND c.name like CONCAT('%',#{ft.kw},'%')
		AND a.createtime > #{ft.startTime}
		AND a.createtime < #{ft.endTime}
		AND a.follow = #{ft.follow}
		AND a.empid = #{ft.empid}
		 AND c.deptid in

            #{id}


      ORDER BY createtime DESC

select  ,b.name productName,c.name empName from cus_customer a  left join cus_product b on a.productid=b.id
left join man_emp c on a.empid=c.id
where a.id=#{id}

mapper.update:
	 UPDATE cus_customer SET
name=#{ model.name }
,typeid=#{ model.typeid }
,follow=#{ model.follow }
,productid=#{ model.productid }
,worth=#{ model.worth }
,contacts=#{ model.contacts }
,phone=#{ model.phone }
,addr=#{ model.addr }
,industry = #{ model.industry }
,scale = #{ model.scale }
,source=#{ model.source}
,state=#{ model.state }
 WHERE id=#{model.id}

UPDATE cus_customer
empid=#{model.empid}, state=#{ model.state },
WHERE id=#{model.id}
