mapper.select:
		select  ,b.name uname,b.identifier,c.name deptName from man_apply a left join man_emp b
		on a.uid = b.id
		left join man_dept c on b.deptid = c.id
		 WHERE 1=1 
		 AND a.uid = #{ft.uid}
		AND a.type = #{ ft.applytype }

		AND b.deptid in

            #{id}


		AND b.name like CONCAT('%',#{ft.kw},'%')
		AND a.createtime > #{ft.starttime}
		AND a.createtime < #{ft.endtime}
		AND b.deptid in

            #{id}


		ORDER BY a.createtime DESC

mapper.update:
UPDATE man_apply
starttime=#{ model.starttime }, endtime=#{ model.endtime }, info=#{ model.info }, leave_type = #{ model.leave_type }, state = #{ model.state },
WHERE id=#{model.id}
