mapper.select:
select  ,d.name empName,b.name prjName,c.name cusName from prj_collect a left join prj_project b on a.prjid = b.id
left join cus_customer c on b.customrid = c.id
left join man_emp d on b.salesmanid=d.id

WHERE 1=1
 AND a.prjid = #{ft.prjid}
 AND c.name like CONCAT('%',#{ft.kw},'%')
 AND d.name like CONCAT('%',#{ft.empName},'%')
 AND a.time > #{ft.startTime}
 AND a.time < #{ft.endTime}
ORDER BY time DESC

mapper.update:
	   UPDATE prj_collect SET
time=#{model.time }
,money=#{ model.money }
,remark=#{ model.remark }
 WHERE id=#{model.id}
