mapper.select:
select ,b.name customrName,c.name managerName,d.name salesmanName,e.name orderempName,f.name progressName from prj_project a left join cus_customer b on a.customrid=b.id left join man_emp c on a.managerid = c.id left join man_emp d on a.salesmanid = d.id left join man_emp e on a.orderempid = e.id left join prj_progress f on a.progressid = f.id WHERE 1=1 AND a.name like CONCAT('%',#{ft.kw},'%') AND c.name like CONCAT('%',#{ft.managerKw},'%') AND d.name like CONCAT('%',#{ft.salesmanKw},'%') AND b.name like CONCAT('%',#{ft.cusKw},'%')
AND a.ordertime > #{ft.startTime} AND a.ordertime < #{ft.endTime} AND a.progressid = #{ft.progressid}
AND (a.managerid = #{ft.uid} OR #{ft.uid} in (select empid from prj_project_meb where prjid=a.id)) AND (a.managerid = #{ft.uid2} OR exists (select bb.deptid from prj_project_meb aa left join man_emp bb on aa.empid=bb.id where aa.prjid=a.id AND bb.deptid in
#{deptid})
) ORDER BY signtime DESC

mapper.update:
	  UPDATE prj_project SET name=#{ model.name }
,customrid=#{ model.customrid }
,productid=#{ model.productid }
,starttime=#{ model.starttime }
,endtime=#{ model.endtime }
,managerid=#{ model.managerid }
,pro_state=#{ model.pro_state }
,receivable=#{ model.receivable }
,uncollected=#{ model.uncollected }
,salesmanid=#{ model.salesmanid }
,signtime=#{ model.signtime }
,progressid=#{ model.progressid }
,member=#{ model.member }
,remark=#{ model.remark }
,renew=#{ model.renew }
,renewtime=#{ model.renewtime }
,renew_content=#{ model.renew_content }
 WHERE id=#{model.id}
